template.description:
processes HTTP requests to Get and Set the data NVRJS needs

API routes required:
${
http.request.uri:startsWith('/api/nvrjs/')
:or(${http.request.uri:equals('/api/mediamtx/webhook')})
}

entry.value:
let filename = split(.segmentPath,"/")[-1]
let startUnix = parse-time(replace($filename,"\\.mp4",""),"yyyy-MM-dd_kk-mm-ss-S")

{
"SegmentID": uuid(),
"CameraID": replace(.streamPath,"/","-"),
"FileName": $filename,
"Start": $startUnix,
"End": number(.segmentDuration) + $startUnix
}

let pathArray = split(.streamPath,"/")
{
"_id": replace(.streamPath,"/","-"),
"name": join([$pathArray[1],$pathArray[0]]," "),
"isLive": true,
"input": join(["rtsp://192.168.1.110:8554/", .streamPath], ""),
"recordingPath": .streamPath,
"inputConfig": {
    "use_wallclock_as_timestamps": "1",
    "fflags": "+igndts",
    "analyzeduration": "1000000",
    "probesize": "1000000",
    "rtsp_transport": "tcp"
			},
"liveConfig": {
  "codecString": "video/mp4; codecs=\"avc1.64001f\"",
  "streamConfig": {
    "an": "",
    "vcodec": "copy",
    "f": "mp4",
    "movflags": "+frag_keyframe+empty_moov+default_base_moof",
    "reset_timestamps": "1"
    }
  }
}
